      out.write("  <script>\n");
      out.write("    // Set current date\n");
      out.write("    function setCurrentDate() {\n");
      out.write("      const dateOptions = { \n");
      out.write("        weekday: 'long', \n");
      out.write("        year: 'numeric', \n");
      out.write("        month: 'long', \n");
      out.write("        day: 'numeric' \n");
      out.write("      };\n");
      out.write("      const currentDate = new Date().toLocaleDateString('en-US', dateOptions);\n");
      out.write("      document.getElementById('currentDate').innerText = currentDate;\n");
      out.write("    }\n");
      out.write("    \n");
      out.write("    // Toggle between calendar preview and full view\n");
      out.write("    function toggleCalendarView() {\n");
      out.write("      const preview = document.getElementById('calendarPreview');\n");
      out.write("      const full = document.getElementById('calendarFull');\n");
      out.write("      const viewText = document.getElementById('viewText');\n");
      out.write("      const viewIcon = document.getElementById('viewIcon');\n");
      out.write("      \n");
      out.write("      if (preview.style.display === 'none') {\n");
      out.write("        preview.style.display = 'block';\n");
      out.write("        full.style.display = 'none';\n");
      out.write("        viewText.innerText = 'View All';\n");
      out.write("        viewIcon.className = 'fas fa-chevron-down';\n");
      out.write("      } else {\n");
      out.write("        preview.style.display = 'none';\n");
      out.write("        full.style.display = 'block';\n");
      out.write("        viewText.innerText = 'View Less';\n");
      out.write("        viewIcon.className = 'fas fa-chevron-up';\n");
      out.write("      }\n");
      out.write("    }\n");
      out.write("    \n");
      out.write("    document.addEventListener('DOMContentLoaded', setCurrentDate);\n");
      out.write("  </script>\n");
